<template>
  <div id="wrapper" ref="wrapper" style="overflow:hidden" :style="{minHeight: innerHeight}">
    <transition name="fade" mode="out-in">
      <navi v-if="nav"></navi>
    </transition>
    <transition name="fade" mode="out-in">
      <Nuxt />
    </transition>
  </div>
</template>
<script>
import Navi from '../components/Navi'
export default {
  components:{
    Navi
  },
  data() {
    return {
      nav: true,
    }
  },
  computed:{
    innerHeight(){
      return this.$store.getters.innerHeight + 'px';
    }
  },
  created(){
    window.addEventListener('resize', this.resizeWindow);
    this.resizeWindow()
  },
  watch:{
    $route:{
      handler(to, from) {
        if(to.name=='index'){
          this.nav=false;
        } else {
          this.nav=true;
        }
      },
      immediate: true
    }
  },
  methods:{
    resizeWindow(){
      this.$store.dispatch('resize', {innerWidth: window.innerWidth, innerHeight: window.innerHeight})
    },
  }
}
</script>
